import React, { useState } from 'react'
import Navbar from '../../shared/components/Navbar'
import  '../../styles/Registro.css'

function Registro() {
  const [idUsuario,setId]=useState(1);
  const[values,setvalues]= useState({
    descripcion:'',
    monto:0,
    fecha:'',
    descuento:0,
    tipo:'',
    idUsuario:idUsuario

  })

  const navigate =useNavigate();
  const[errors,setErrors]=useState({})

    const handleInput=(event)=>{
    setvalues(prev =>({...prev,[event.target.name]:[event.target.value]}))
  }

  const handleSubmit=async(event)=>{
    event.preventDefault();
    setErrors(Validation(values));
    if(errors.name === "" && errors.email === "" && errors.password === ""){
      axios.post('http://localhost:8081/Registro',values)
      .then(res=> {
        navigate('/');
         //console.log(values)
      })
      .catch(err=>console.log(err))
    }
  } 


  return (
    <div>
        <Navbar/>
        <div className="form" onSubmit={handleSubmit}>
        <h1 className="titulo">Registro Movimiento</h1>
        <form className="formu" method="post">       

          <label className="labels">Descripcion</label><br></br>
          <input className="mo" id='descripcion' type="text" onChange={handleInput}/><br></br>

          <label className="labels">Monto</label><br></br>
          <input className="mo" id='monto' type="int"onChange={handleInput}/><br></br>

          <label className='labels'>Fecha</label><br></br>
          <input className="mo" id='fecha' type="date" onChange={handleInput}/><br></br>

          <label className='labels'>Descuento</label><br></br>
          <input className="mo" id='descuento' type="text" onChange={handleInput}/><br></br>

        <div className="checks">
          <input type="checkbox" id="cbox2" value="second_checkbox"></input>
          <label>Ingreso</label>
          <input className="cajas" type="checkbox" id="cbox2" value="second_checkbox"></input>
          <label>Gasto</label>
        </div>
          
          <div className="posicionBoton">
          <button className="boton" type="submit" >Cancelar</button><br></br>
          <button className="boton" type="submit">Registrar</button><br></br>
          </div>
        </form>
      </div>
    </div>
  )
}

export default Registro
